	WHERE ID_MES= mes AND ID_ANHO = anho
			AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS IN (1121,1122) AND ID_PERSONA = P_ID_PERSONA;
		IF l_contar >0 THEN
			SELECT SUM(COS_VALOR) INTO l_prima FROM APS_PLANILLA_DETALLE 
						WHERE ID_MES= mes AND ID_ANHO = anho
							AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS IN (1121,1122) AND ID_PERSONA = P_ID_PERSONA;
