SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

function  FC_PSTO_DIST_MESNSUAL(P_ID_ACTIVIDAD NUMBER) RETURN VARCHAR2
IS
l_dato varchar2(3000):='';
l_conta int;



BEGIN
    SELECT COUNT(*) INTO l_conta FROM PSTO_ACTIVIDAD_DIST
    WHERE ID_ACTIVIDAD=P_ID_ACTIVIDAD;
    
    IF l_conta>0 THEN
        SELECT X.MESES INTO l_dato FROM (                
        SELECT LISTAGG(CASE WHEN B.TIPO_DIST='PO' THEN M.SIGLAS||' '||A.PORCENTAJE||'%' ELSE M.SIGLAS END, ', ') WITHIN GROUP(ORDER BY A.ID_MES) OVER(PARTITION BY A.ID_ACTIVIDAD) MESES,A.ID_ACTIVIDAD 
        FROM PSTO_ACTIVIDAD_DIST A, PSTO_ACTIVIDAD B, CONTA_MES M 
        WHERE A.ID_ACTIVIDAD=B.ID_ACTIVIDAD
        AND A.ID_MES=M.ID_MES
        ORDER BY A.ID_ACTIVIDAD
      )X WHERE X.ID_ACTIVIDAD=P_ID_ACTIVIDAD
      GROUP BY X.MESES;
    END IF;	
	
	RETURN(l_dato);
END;

