FUNCTION        FC_RS_PROVEEDOR(P_ID_PROVEEDOR NUMBER) RETURN VARCHAR2 AS
  l_datos VARCHAR2(900);
 cursor datos2 is
                SELECT  
                p.nombre
                FROM MOISES.PERSONA_JURIDICA A
                INNER JOIN moises.persona p ON p.id_persona = A.id_persona
                WHERE A.ID_PERSONA=P_ID_PROVEEDOR;
cursor datos is
                SELECT  
                p.paterno|| ' ' || p.materno || ', ' ||p.nombre
                FROM MOISES.PERSONA_DOCUMENTO A
                INNER JOIN moises.persona p ON p.id_persona = A.id_persona
                WHERE A.ID_PERSONA=P_ID_PROVEEDOR
                 and A.ID_TIPODOCUMENTO = 6;

BEGIN
       open datos;  
       fetch datos into l_datos;
       if not datos%found then
            open datos2;  
            fetch datos2 into l_datos;
            if not datos2%found then
                l_datos := ' ';
            end if;
            close datos2;
       end if;
       close datos;
       RETURN( l_datos );
END;

