FUNCTION FC_SALDO_COMPROBANTE(P_ANIO INTEGER,P_ID_CLIENTE INTEGER, P_ID_VENTA INTEGER)
    RETURN NUMBER IS
    SALDO NUMBER(10,2);		 

    BEGIN

    DECLARE
    
    L_TOTAL NUMBER(10,2);

    BEGIN
        
        SELECT SUM(TOTAL) INTO L_TOTAL FROM VW_SALES_MOV  WHERE ID_ANHO = P_ANIO AND ID_CLIENTE = P_ID_CLIENTE AND ID_VENTA = P_ID_VENTA;
        
        IF L_TOTAL = 0 THEN
            --SELECT TOTAL INTO SALDO FROM VENTA WHERE ID_ANHO = P_ANIO AND ID_CLIENTE = P_ID_CLIENTE AND ID_VENTA = P_ID_VENTA;
            SELECT ABS(SUM(TOTAL)) INTO SALDO FROM VW_SALES_MOV  WHERE ID_ANHO = P_ANIO AND ID_CLIENTE = P_ID_CLIENTE AND ID_VENTA = P_ID_VENTA AND TOTAL < 0;
        ELSE
            SALDO := 0;
        END IF;
        RETURN SALDO;
    END;

END;

