FUNCTION        FC_SPLIT(p_list VARCHAR2, p_del VARCHAR2 := ',')
   RETURN tablastring
   PIPELINED IS
   l_idx PLS_INTEGER;
   l_list VARCHAR2 (32767) := p_list;
BEGIN
