FUNCTION        FC_SUELDO_BASICO(P_ID_ENTIDAD NUMBER, P_ID_PERSONA NUMBER, FECHA_INI DATE, FECHA_FIN DATE) RETURN NUMBER
IS
l_sueldo number(10,2):=0.00;
l_contar int:=0;
anho number;
mes number;
activo NUMBER;
BEGIN
	anho:=EXTRACT(YEAR FROM FECHA_FIN);
	mes:=EXTRACT(MONTH FROM FECHA_FIN);
	SELECT COUNT(*) INTO l_contar FROM APS_PLANILLA_DETALLE 
	WHERE ID_MES= mes AND ID_ANHO = anho
			AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA;
	IF l_contar >0 THEN
		SELECT SUM(COS_VALOR) INTO l_sueldo FROM APS_PLANILLA_DETALLE 
					WHERE ID_MES= mes AND ID_ANHO = anho
						AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA;
	ELSE
		SELECT COUNT(*) INTO activo FROM APS_EMPLEADO WHERE ESTADO ='A' AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_PERSONA = P_ID_PERSONA AND 
		FECHA_FIN BETWEEN FEC_ENTIDAD AND NVL(FEC_TERMINO,SYSDATE);
		IF activo>0 THEN 
			SELECT SUBSTR((SELECT "MAX"(TO_NUMBER(ID_ANHO||LPAD(ID_MES,2,0))) FROM APS_PLANILLA_DETALLE 
					WHERE (TO_NUMBER(ID_ANHO||LPAD(ID_MES,2,0)) BETWEEN 
					TO_NUMBER(EXTRACT(YEAR FROM FECHA_INI)||LPAD(EXTRACT(MONTH FROM FECHA_INI),2,0))
					AND TO_NUMBER(EXTRACT(YEAR FROM FECHA_FIN)||LPAD(EXTRACT(MONTH FROM FECHA_FIN),2,0)))
					AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA),0,4) "Substring" into anho
						FROM DUAL;

			SELECT SUBSTR((SELECT "MAX"(TO_NUMBER(ID_ANHO||LPAD(ID_MES,2,0))) FROM APS_PLANILLA_DETALLE 
					WHERE (TO_NUMBER(ID_ANHO||LPAD(ID_MES,2,0)) BETWEEN 
					TO_NUMBER(EXTRACT(YEAR FROM FECHA_INI)||LPAD(EXTRACT(MONTH FROM FECHA_INI),2,0))
					AND TO_NUMBER(EXTRACT(YEAR FROM FECHA_FIN)||LPAD(EXTRACT(MONTH FROM FECHA_FIN),2,0)))
					AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA),5,6) "Substring" into mes
						FROM DUAL;
			SELECT COUNT(*) INTO l_contar FROM APS_PLANILLA_DETALLE 
			WHERE ID_MES= mes AND ID_ANHO = anho
					AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA;
			IF l_contar >0 THEN
				SELECT SUM(COS_VALOR) INTO l_sueldo FROM APS_PLANILLA_DETALLE 
							WHERE ID_MES= mes AND ID_ANHO = anho
								AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA;
			END IF;
		END IF;
	END IF;

	/*IF EXTRACT(YEAR FROM FECHA_INI) = EXTRACT(YEAR FROM FECHA_FIN) THEN
		--CALCULA ENTRE DOS FECHAS DEL MISMO AÑO
