		WHERE ID_MES BETWEEN EXTRACT(MONTH FROM FECHA_INI) AND EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO = EXTRACT(YEAR FROM FECHA_INI)
				AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA;
		IF l_contar > 0 THEN
			IF l_contar = 1 THEN
					SELECT COS_REFERENCIA2 INTO l_sueldo FROM  APS_PLANILLA_DETALLE 
					WHERE ID_MES BETWEEN EXTRACT(MONTH FROM FECHA_INI) AND EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO = EXTRACT(YEAR FROM FECHA_INI)
						AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA;
			ELSE
				SELECT SUBSTR((SELECT "MAX"("CONCAT"(ID_ANHO, ID_MES)) FROM APS_PLANILLA_DETALLE 
				WHERE ID_MES BETWEEN EXTRACT(MONTH FROM FECHA_INI) AND EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO = EXTRACT(YEAR FROM FECHA_INI)
					AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA),0,4) "Substring" into anho
					FROM DUAL;

				SELECT SUBSTR((SELECT "MAX"("CONCAT"(ID_ANHO, ID_MES)) FROM APS_PLANILLA_DETALLE 
				WHERE ID_MES BETWEEN EXTRACT(MONTH FROM FECHA_INI) AND EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO = EXTRACT(YEAR FROM FECHA_INI)
					AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA),5,2) "Substring" INTO mes
					FROM DUAL;

				SELECT COS_REFERENCIA2 INTO l_sueldo FROM  APS_PLANILLA_DETALLE 
				WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA AND ID_ANHO = anho AND ID_MES = mes;
			END IF;
		END IF;
	ELSE
		--CALCULA ENTRE DOS FECHAS DE DIFERENTES AÑOS
		SELECT COUNT(*) INTO l_contar FROM APS_PLANILLA_DETALLE 
		WHERE ((ID_MES >=EXTRACT(MONTH FROM FECHA_INI) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_INI)) OR (ID_MES <= EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_FIN))) 
				AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA;
		IF l_contar > 0 THEN
			IF l_contar = 1 THEN
					SELECT COS_REFERENCIA2 INTO l_sueldo FROM  APS_PLANILLA_DETALLE 
					WHERE ((ID_MES >=EXTRACT(MONTH FROM FECHA_INI) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_INI)) OR (ID_MES <= EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_FIN))) 
						AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA;
			ELSE
				SELECT SUBSTR((SELECT "MAX"("CONCAT"(ID_ANHO, ID_MES)) FROM APS_PLANILLA_DETALLE 
				WHERE ((ID_MES >=EXTRACT(MONTH FROM FECHA_INI) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_INI)) OR (ID_MES <= EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_FIN))) 
					AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA),0,4) "Substring" into anho
					FROM DUAL;

				SELECT SUBSTR((SELECT "MAX"("CONCAT"(ID_ANHO, ID_MES)) FROM APS_PLANILLA_DETALLE 
				WHERE ((ID_MES >=EXTRACT(MONTH FROM FECHA_INI) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_INI)) OR (ID_MES <= EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_FIN))) 
					AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA),5,2) "Substring" INTO mes
					FROM DUAL;

				SELECT COS_REFERENCIA2 INTO l_sueldo FROM  APS_PLANILLA_DETALLE 
				WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1000 AND ID_PERSONA = P_ID_PERSONA AND ID_ANHO = anho AND ID_MES = mes;
			END IF;
		END IF;
	END IF;*/
	RETURN (l_sueldo);
END;

