SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_ULTIMA_GRATI(P_ID_ENTIDAD NUMBER, P_ID_PERSONA NUMBER, FECHA_INI DATE) RETURN NUMBER
IS
l_grati number(10,2):=0.00;
l_contar int:=0;
anho number;
mes number;

BEGIN
	SELECT NVL(CAST(SUM(COS_VALOR) AS FLOAT), 0) INTO   l_grati
			FROM APS_PLANILLA_DETALLE
			WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS IN (3000, 3079, 3086, 3121, 3122, 3126, 3145, 3151, 3157, 3171, 3212, 3222) AND ID_PERSONA = P_ID_PERSONA
			AND ID_MES = (CASE WHEN EXTRACT(MONTH FROM FECHA_INI) <7 OR EXTRACT(MONTH FROM FECHA_INI) =12 THEN 12 ELSE 7 END)
			AND ID_ANHO = (CASE WHEN EXTRACT(MONTH FROM FECHA_INI)<7 THEN EXTRACT(YEAR FROM FECHA_INI) -1 ELSE EXTRACT(YEAR FROM FECHA_INI) END);

	RETURN (l_grati);
END;

