SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FT_ALUMNO_EAP3
(
  P_ID_PERSONA IN NUMBER 
) RETURN VARCHAR2 AS 
l_eap varchar2(500);
BEGIN
    SELECT c.NOMBRE into l_eap
    FROM DAVID.ACAD_ALUMNO_PLAN A inner join DAVID.ACAD_PLAN_PROGRAMA B on  A.ID_PLAN_PROGRAMA=B.ID_PLAN_PROGRAMA
          AND A.ID_PERSONA=P_ID_PERSONA
          AND A.ESTADO='1'
    inner join DAVID.ACAD_PROGRAMA_ESTUDIO C on B.ID_PROGRAMA_ESTUDIO=C.ID_PROGRAMA_ESTUDIO
    where ROWNUM=1;  
  RETURN l_eap;
END FT_ALUMNO_EAP3;

