SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION FT_ALUMNO_SCHOOL_INSTITUCION 
(
  P_ID_PERSONA IN NUMBER 
) RETURN VARCHAR2 AS 
l_contar number:=0;
l_institucion varchar2(500);
BEGIN

    SELECT COUNT(1) INTO l_contar
    FROM JOSE.SCHOOL_ALUMNO A
    INNER JOIN JOSE.SCHOOL_INSTITUCION B ON A.ID_INSTITUCION = B.ID_INSTITUCION 
    WHERE A.ID_ALUMNO =P_ID_PERSONA;

    if l_contar>0 then

        SELECT B.NOMBRE INTO l_institucion
        FROM JOSE.SCHOOL_ALUMNO A
        INNER JOIN JOSE.SCHOOL_INSTITUCION B ON A.ID_INSTITUCION = B.ID_INSTITUCION 
        WHERE A.ID_ALUMNO =P_ID_PERSONA
        AND ROWNUM <=1;

    end if;    
  RETURN l_institucion;
END FT_ALUMNO_SCHOOL_INSTITUCION;

