SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION FT_ALUMNO_SCHOOL_NIVELGRADO 
(
  P_ID_PERSONA IN NUMBER 
) RETURN VARCHAR2 AS 
l_contar number:=0;
l_nivel_grado varchar2(500);
BEGIN

    SELECT count(1) into l_contar
    FROM JOSE.SCHOOL_ALUMNO A
    INNER JOIN JOSE.SCHOOL_NIVEL B ON A.ID_NIVEL = B.ID_NIVEL 
    INNER JOIN JOSE.SCHOOL_GRADO C ON A.ID_GRADO = C.ID_GRADO 
    WHERE A.ID_ALUMNO =P_ID_PERSONA;

    if l_contar>0 then

        SELECT B.NOMBRE || ' - ' || C.NOMBRE INTO l_nivel_grado
        FROM JOSE.SCHOOL_ALUMNO A
        INNER JOIN JOSE.SCHOOL_NIVEL B ON A.ID_NIVEL = B.ID_NIVEL 
        INNER JOIN JOSE.SCHOOL_GRADO C ON A.ID_GRADO = C.ID_GRADO 
        WHERE A.ID_ALUMNO =P_ID_PERSONA
        AND ROWNUM <=1;

    end if;    
  RETURN l_nivel_grado;
END FT_ALUMNO_SCHOOL_NIVELGRADO;

