SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION         sexo( v_id_persona in integer) 
RETURN varchar2 IS
	p_sexo	  varchar2(800);

BEGIN

    select decode(sexo, 1, 'M', 2, 'F', '-')  into p_sexo
    from moises.persona_natural
    where id_persona=v_id_persona;

return p_sexo;

END;



