SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION         tipo_doc_cliente( v_id_persona in integer) 
RETURN varchar2 IS
	p_tipo_doc	  varchar2(800);

BEGIN

    select decode(id_tipodocumento, 1, 'DNI', 4, 'CE', 6, 'RUC', 7, 'PASS', '11', 'PN', 'OTROS')  into p_tipo_doc
    from moises.persona_natural
    where id_persona=v_id_persona;

return p_tipo_doc;

END;



