          from vv_importa_venta_jesus
          order by fecha;
          */
        L_ERROR NUMBER;
        L_MSGERROR VARCHAR2(100) :='';
            
    BEGIN
            /*
            FOR sal in saldos
                LOOP
                
              SELECT count(v.id_venta) into l_count FROM VENTA v WHERE v.ID_ENTIDAD =sal.id_entidad
              --AND ID_DEPTO =sal.id_depto
