\set ON_ERROR_STOP ON

PROCEDURE        IUDP_DIARIO_DETALLE (P_Modo int, P_ID_ENTIDAD int, P_COD_AASI int, P_ID_TIPOASIENTO varchar2, P_FEC_ASIENTO varchar2, P_ID_DEPARTAMENTO varchar2, P_ID_FONDO INT, P_ID_TIPOPLAN INT, 
P_ID_CUENTAAASI varchar2, P_ID_RESTRICCION varchar2, P_ID_CUENTACTE VARCHAR2, P_NUM_AASI VARCHAR2, P_VALOR VARCHAR2, P_VALOR_DOLAR VARCHAR2, P_COMENTARIO VARCHAR2, P_MEMO VARCHAR2) AS

S_ID_DIARIO         INT;
S_ID_DIARIODETALLE  INT;
S_COS_VALOR         NUMERIC(10,2);
S_COS_DOLAR         NUMERIC(10,2);
S_ID_CUENTACTE      VARCHAR(255);
S_MEMO              VARCHAR(255);
S_COMENTARIO        VARCHAR(1000);
S_CANT              INT;
S_ANHO              INT;
S_MES               INT;
                                                   
BEGIN  
  
    IF P_Modo = 0 THEN
        S_ID_CUENTACTE := CASE WHEN P_ID_CUENTACTE = '@JournalItemSubAccountCode@' OR P_ID_CUENTACTE = '0' THEN '' ELSE P_ID_CUENTACTE END;
        S_COS_VALOR    := TO_NUMBER(P_VALOR,'9999999999.9999');
        S_COS_DOLAR    := CASE WHEN P_VALOR_DOLAR = '@JournalItemAccountValue@' OR P_VALOR_DOLAR = '' THEN 0 ELSE TO_NUMBER(P_VALOR_DOLAR,'9999999999.9999') END;
        S_MEMO         := CASE WHEN P_MEMO = '@JournalItemMemo@' THEN '' ELSE P_MEMO END;
        S_COMENTARIO   := P_COMENTARIO;     
        S_ANHO         := TO_CHAR(to_date(P_FEC_ASIENTO,'yyyy-mm-dd"T"HH24:MI:SS'),'YYYY');      
        S_MES          := TO_NUMBER(TO_CHAR(to_date(P_FEC_ASIENTO,'yyyy-mm-dd"T"HH24:MI:SS'),'MM'));
          
        SELECT    
                COUNT(ID_DIARIO), max(id_diario)  INTO S_CANT, S_ID_DIARIO
        FROM CONTA_DIARIO
        WHERE ID_ENTIDAD = P_ID_ENTIDAD
        AND ID_ANHO = S_ANHO
        AND ID_MES = S_MES
        AND ID_TIPOASIENTO = P_ID_TIPOASIENTO
        AND COD_AASI = P_COD_AASI;

        
        IF S_CANT > 0 THEN
        
            SELECT
                    NVL(MAX(ID_DIARIO_DETALLE),0)+1 INTO S_ID_DIARIODETALLE
            FROM CONTA_DIARIO_DETALLE
            WHERE ID_ENTIDAD = P_ID_ENTIDAD
            AND ID_DIARIO = S_ID_DIARIO;    
            
            INSERT INTO CONTA_DIARIO_DETALLE (ID_ENTIDAD, ID_DIARIO, ID_DIARIO_DETALLE, ID_DEPTO, ID_FONDO, ID_TIPOPLAN, ID_CUENTAAASI, ID_RESTRICCION, ID_CTACTE, NUM_AASI, COS_VALOR, COS_DOLAR, OBSERVACION, COMENTARIO)
            VALUES (P_ID_ENTIDAD, S_ID_DIARIO, S_ID_DIARIODETALLE, P_ID_DEPARTAMENTO, P_ID_FONDO, P_ID_TIPOPLAN, P_ID_CUENTAAASI, P_ID_RESTRICCION, NVL(S_ID_CUENTACTE, ''), REPLACE(P_NUM_AASI, '-', '.'), S_COS_VALOR, S_COS_DOLAR, NVL(S_MEMO, ''), S_COMENTARIO);
        END IF;
    END IF;
END;

