SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

PROCEDURE        iudp_Moneda (P_Modo IN int, P_ID_MONEDA IN int, P_COD_INTERNO IN varchar2, P_NOMBRE IN varchar2, P_SIGLAS IN varchar2, P_SIMBOLO IN varchar2, P_COD_SUNAT IN varchar2) AS
 
	S_SIGLAS           varchar2(60);
	S_CANTIDAD            INT;
	S_ID_MONEDA            INT;
    
 BEGIN
	 
IF P_Modo = 0 THEN

	S_SIGLAS := CASE WHEN P_SIGLAS IN ('@Symbol@') THEN '' ELSE P_SIGLAS END;
    
    SELECT COUNT(*) INTO S_CANTIDAD FROM Conta_Moneda WHERE COD_INTERNO = P_COD_INTERNO; 
    
    IF S_CANTIDAD = 0 THEN
    
    	SELECT COALESCE(max(ID_MONEDA),0)+1 INTO S_ID_MONEDA FROM CONTA_MONEDA;
    
      INSERT INTO Conta_Moneda (ID_MONEDA, NOMBRE, SIGLAS, SIMBOLO, COD_INTERNO, COD_SUNAT)
        VALUES (S_ID_MONEDA, P_NOMBRE, S_SIGLAS, P_SIMBOLO, P_COD_INTERNO, P_COD_SUNAT);
    
    ELSE
      UPDATE Conta_Moneda SET
        NOMBRE      = P_NOMBRE,
        SIGLAS      = S_SIGLAS,
        SIMBOLO     = COALESCE(TRIM(P_SIMBOLO), SIMBOLO) ,
        COD_SUNAT   = COALESCE(TRIM(P_COD_SUNAT), COD_SUNAT),
        COD_INTERNO = P_COD_INTERNO
      WHERE ID_MONEDA = P_ID_MONEDA;
    END IF;  

  END IF;

   
 end;

