SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

PROCEDURE iudp_Restriccion (P_Modo IN int, P_ID_RESTRICCION IN varchar2, P_ID_PARENT IN varchar2, P_NOMBRE IN varchar2) AS
 
  S_ID_PARENT           varchar2(60);
  S_CANTIDAD            INT;
    
 BEGIN
	 
IF P_Modo = 0 THEN
    S_ID_PARENT := CASE WHEN P_ID_PARENT IN ('@ParentCode@') THEN '' ELSE P_ID_PARENT END;
    
    SELECT COUNT(*) INTO S_CANTIDAD FROM Conta_Restriccion WHERE ID_RESTRICCION = P_ID_RESTRICCION; 
    
    IF S_CANTIDAD = 0 THEN
    
      INSERT INTO Conta_Restriccion (ID_RESTRICCION, ID_PARENT, NOMBRE, ES_GRUPO)
        VALUES (P_ID_RESTRICCION, NULLIF(S_ID_PARENT, ''), P_NOMBRE, 0);
    
    ELSE
      UPDATE Conta_Restriccion SET
        NOMBRE = P_NOMBRE
      WHERE ID_RESTRICCION = P_ID_RESTRICCION;
    END IF;  
    
    UPDATE Conta_Restriccion SET
      ES_GRUPO = 1
    WHERE ID_RESTRICCION = S_ID_PARENT;
  END IF;

   
 end;

