PROCEDURE        iudp_Tipo_Activo_Movimiento (P_Modo IN int, P_ID_TIPOACTIVOMOVIMIENTO IN varchar2, P_NOMBRE IN varchar2, P_ES_ACREEDORA IN varchar2) AS
 
  S_CANTIDAD            INT;
    
 BEGIN
	 
IF P_Modo = 0 THEN
    
    SELECT COUNT(*) INTO S_CANTIDAD FROM Tipo_Activo_Movimiento WHERE ID_TIPOACTIVOMOVIMIENTO = P_ID_TIPOACTIVOMOVIMIENTO; 
    
    IF S_CANTIDAD = 0 THEN
    
      INSERT INTO Tipo_Activo_Movimiento (ID_TIPOACTIVOMOVIMIENTO, NOMBRE, ES_ACREEDORA)
        VALUES (P_ID_TIPOACTIVOMOVIMIENTO, P_NOMBRE, 0);
    
    ELSE
      UPDATE Tipo_Activo_Movimiento SET
        NOMBRE = P_NOMBRE
      WHERE ID_TIPOACTIVOMOVIMIENTO = P_ID_TIPOACTIVOMOVIMIENTO;
    END IF;  

  END IF;

   
 end;

