SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

procedure PCD_SALES_FINANCES_PJ( pval in integer, v_mes in integer) AS 

  p_res varchar2(100);
  p_anio integer;
  p_mes integer;
  cursor niveles is
    SELECT a.id_depto, nombre
    FROM eliseo.conta_entidad_depto a
    WHERE a.id_entidad = 7124
      AND a.es_grupo = 1
      AND length(a.id_depto) = 1
    ORDER BY a.id_depto;

begin 

if(pval>=2020) then
  SELECT pval,v_mes into p_anio,p_mes
  FROM dual;
else
  SELECT TO_NUMBER(to_char(sysdate,'YYYY')),TO_NUMBER(to_char(sysdate,'MM')) into p_anio,p_mes
  FROM dual;
end if;

  ELISEO.PKG_SALES_FINANCES.SP_RESULTADO_DEPTO(7124,p_anio,p_mes,4);
  commit; 
  ELISEO.PKG_SALES_FINANCES.SP_RESULTADO_DEPARTAMENTO(7124,p_anio,p_mes,4);
  commit; 
  ELISEO.PKG_SALES_FINANCES.SP_RESULTADO_COSTOS_G(7124,p_anio,p_mes,4);
  commit; 
  ELISEO.PKG_SALES_FINANCES.SP_RESULTADO_COSTOS_SL(7124,p_anio,p_mes,4);
  commit; 
  ELISEO.PKG_SALES_FINANCES.SP_RESULTADO_COSTOS_SC(7124,p_anio,p_mes,4);
  commit; 
  ELISEO.PKG_SALES_FINANCES.SP_RESUMEN(7124,p_anio,p_mes,4);
  commit; 
  ELISEO.PKG_SALES_FINANCES.SP_RESULTADO_ALUMNOS(7124,p_anio);
  commit; 


  for req in niveles
  loop      
    ELISEO.PKG_SALES_FINANCES.SP_ESTADO_RESULTADO(7124,p_anio,p_mes,req.id_depto);
    commit;
    ELISEO.PKG_SALES_FINANCES.SP_BALANCE(7124,p_anio,p_mes,req.id_depto);
    commit; 
  end loop;
  
  
  PKG_SALES_FINANCES.SP_ESTADO_RESULTADO_CONS(7124,p_anio,p_mes);
  commit; 
  PKG_SALES_FINANCES.SP_BALANCE_CONSOLIDADO(7124,p_anio,p_mes);
  commit; 

end;

