PROCEDURE        spc_bi_fondo_estimulo (
    p_id_entidad           int,
    p_id_anho              int,
    p_id_mes_fin           int,
    cursor       OUT SYS_REFCURSOR
)
IS
    l_num_documento varchar2(100);

BEGIN
    l_num_documento:= '';


    OPEN cursor FOR 
    SELECT b.id_depto, d.nombre, saldo_ini, ingresos, gastos, saldo FROM (
SELECT a.ID_ENTIDAD, a.ID_DEPTO, sum(si)*-1 AS saldo_ini, sum(ingresos)*-1 AS ingresos, 
sum(gastos)*-1 AS gastos, (sum(si)+sum(ingresos)+sum(gastos))*-1 AS saldo FROM (
SELECT ID_ENTIDAD, ID_DEPTO,COS_VALOR AS si, 0 AS ingresos, 0 AS gastos FROM VW_CONTA_DIARIO
WHERE ID_ENTIDAD = 17112
AND ID_ANHO = p_id_anho
AND ID_TIPOASIENTO = 'BB'
AND id_mes BETWEEN 1 AND p_id_mes_fin
AND ID_DEPTO IN ('138111','138121','138121','138131','138141','138151')
UNION ALL
SELECT ID_ENTIDAD, ID_DEPTO,0 AS si, COS_VALOR AS ingresos, 0 AS gastos FROM VW_CONTA_DIARIO
WHERE ID_ENTIDAD = 17112
AND ID_ANHO = p_id_anho
AND NOT ID_TIPOASIENTO = 'BB'
AND id_mes BETWEEN 1 AND p_id_mes_fin
AND ID_CUENTAAASI LIKE '31%'
AND ID_DEPTO IN ('138111','138121','138121','138131','138141','138151')
UNION ALL
SELECT ID_ENTIDAD, ID_DEPTO,0 AS si, 0 AS ingresos, COS_VALOR AS gastos FROM VW_CONTA_DIARIO
WHERE ID_ENTIDAD = 17112
AND ID_ANHO = p_id_anho
AND NOT ID_TIPOASIENTO = 'BB'
AND id_mes BETWEEN 1 AND p_id_mes_fin
AND ID_CUENTAAASI LIKE '41%'
AND ID_DEPTO IN ('138111','138121','138121','138131','138141','138151')
) a
GROUP BY a.ID_ENTIDAD, a.ID_DEPTO
) b, conta_entidad_depto d 
WHERE b.ID_ENTIDAD = d.ID_ENTIDAD
AND b.ID_DEPTO = d.ID_DEPTO
        ;
  
END;

