SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

procedure        spc_user_session_finish 
is
   l_time_session      integer := 10;--tiempo maximo de session inactiva
begin
   insert into users_session_log (token,
                                  id_user,
                                  id_entidad,
                                  id_depto,
                                  date_create,
                                  last_update,
                                  status,
                                  id_almacen,
                                  token_oauth)
      select token,
             id_user,
             id_entidad,
             id_depto,
             date_create,
             last_update,
             status,
             id_almacen,
             token_oauth
        from users_session
       where last_update < sysdate - (l_time_session / 1440)
       and caduca = 'S';

   delete from users_session
         where last_update < sysdate - (l_time_session / 1440)
         and caduca = 'S';
end;

