SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

PROCEDURE        SP_ANULAR_DEPOSITOS_REG(P_ID_DEPOSITO NUMBER, P_ID_PERSONA NUMBER, P_MOTIVO VARCHAR2, P_ERROR OUT NUMBER,P_MSGERROR OUT VARCHAR2) IS
        L_ID_VOUCHER NUMBER;
        L_ACTIVO VARCHAR2(1);
        L_ID_KARDEX NUMBER;
        L_ID_ALMACEN NUMBER;
        L_ID_ARTICULO NUMBER;
        L_ID_ANHO NUMBER;
        L_ID_TIPOORIGEN NUMBER := 1;
        L_ID_ASIENTO NUMBER;
        L_DATA VARCHAR2(255);

        L_ID_DEPOSITO NUMBER;
        L_ID_VOUCHER_DEP NUMBER;
        L_ID_TIPOORIGEN_DEP NUMBER;
        L_CANT NUMBER;
        L_ID_ARREGLO NUMBER;

        CURSOR asientos_dep is
        SELECT 
                A.ID_ASIENTO
        FROM CONTA_ASIENTO A JOIN CAJA_DEPOSITO B
        ON A.ID_ORIGEN = B.ID_DEPOSITO
        WHERE A.ID_TIPOORIGEN = L_ID_TIPOORIGEN_DEP
        AND A.ID_ORIGEN = P_ID_DEPOSITO
        AND A.VOUCHER = L_ID_VOUCHER_DEP;

        BEGIN
        -- VALIDAR SI EL VOUCHER ESTÁ CONTABILIZADO
        L_ACTIVO := 'S';

            SELECT V.ACTIVO INTO L_ACTIVO FROM CAJA_DEPOSITO D 
            INNER JOIN CONTA_VOUCHER V ON D.ID_VOUCHER = V.ID_VOUCHER 
            WHERE D.ID_DEPOSITO = P_ID_DEPOSITO;

            IF L_ACTIVO = 'S' THEN
                --ANULAR DEPOSITO
                SELECT COUNT(1) INTO L_CANT FROM CAJA_DEPOSITO_DETALLE
                WHERE ID_DEPOSITO = P_ID_DEPOSITO;

                IF L_CANT >= 0 THEN
                
                    SELECT NVL(MAX(ID_ARREGLO),0)+1  INTO L_ID_ARREGLO FROM ARREGLO;

                    -- Insertando en arreglo:
                    Insert into ARREGLO (ID_ARREGLO,
                    ID_ENTIDAD, ID_DEPTO, ID_TIPOARREGLO, ID_PERSONA, ID_MODULO, ID_ORIGEN, ID_TIPOORIGEN, 
                    MOTIVO, FECHA, INFO_BACKUP, ESTADO
                    ) 
                    select L_ID_ARREGLO, ID_ENTIDAD, id_depto, 1 as id_tipoarreglo, P_ID_PERSONA, 62, id_deposito, id_tipoorigen, P_MOTIVO, sysdate, 
                    'IMPORTE: '||importe||' Glosa: '||glosa, 2
                    from caja_deposito 
                    where id_deposito = P_ID_DEPOSITO;

                    SELECT ID_VOUCHER,ID_TIPOORIGEN INTO L_ID_VOUCHER_DEP,L_ID_TIPOORIGEN_DEP
                    FROM CAJA_DEPOSITO
                    WHERE ID_DEPOSITO = P_ID_DEPOSITO;

                    OPEN asientos_dep;
                      FETCH asientos_dep INTO L_ID_ASIENTO;
                        WHILE asientos_dep%FOUND LOOP

                            UPDATE CONTA_ASIENTO SET IMPORTE = 0, DESCRIPCION = '<< Anulado>>', IMPORTE_ME = 0
                            WHERE ID_ASIENTO = L_ID_ASIENTO;

                            FETCH asientos_dep INTO L_ID_ASIENTO;
                        END LOOP;
                    CLOSE asientos_dep;

                    UPDATE CAJA_DEPOSITO_DETALLE SET IMPORTE = 0, IMPORTE_ME = 0
                    WHERE ID_DEPOSITO = P_ID_DEPOSITO;

                    UPDATE CAJA_DEPOSITO SET IMPORTE = 0, IMPORTE_ME = 0, GLOSA = '<< Anulado >>'
                    WHERE ID_DEPOSITO = P_ID_DEPOSITO;
                END IF;
                P_ERROR := 0;
                P_MSGERROR := 'OK: Anulado con Exito';
            ELSE
                P_ERROR := 1;
                P_MSGERROR := 'El Voucher ya esta CONTABILIZADO';
            END IF;
    END SP_ANULAR_DEPOSITOS_REG;

