DROP TRIGGER IF EXISTS tr_nro_reposicion ON caja_reposicion CASCADE;
CREATE OR REPLACE FUNCTION eliseo.trigger_fct_tr_nro_reposicion() RETURNS trigger AS $BODY$
DECLARE 
L_NRO_DOC NUMBER;
BEGIN
	SELECT MAX(TO_NUMBER(NUMERO)) INTO L_NRO_DOC FROM ELISEO.CAJA_REPOSICION 
	WHERE ID_ENTIDAD = :new.ID_ENTIDAD AND ID_DEPTO = :new.ID_DEPTO; 
	SELECT COALESCE(L_NRO_DOC,0)+1
  	INTO :new.NUMERO
  	FROM dual;
END
$BODY$
