CREATE OR REPLACE VIEW vw_inventario_articulo (id_articulo, id_parent, id_unidadmedida, nombre_unidadmedida, id_marca, nombre_marca, id_codcubso, id_clase, nombre_clase, nombre, codigo, estado, square) AS SELECT A.ID_ARTICULO,
            A.ID_PARENT,
            B.ID_UNIDADMEDIDA,
            B.NOMBRE AS NOMBRE_UNIDADMEDIDA,
            D.ID_MARCA,
            D.NOMBRE AS NOMBRE_MARCA,
            A.ID_CODCUBSO,
            C.ID_CLASE,
            C.NOMBRE AS NOMBRE_CLASE,
            A.NOMBRE,
            A.CODIGO,
            A.ESTADO,
            '0' AS SQUARE
       FROM INVENTARIO_ARTICULO A
            LEFT JOIN INVENTARIO_UNIDAD_MEDIDA B
               ON A.ID_UNIDADMEDIDA = B.ID_UNIDADMEDIDA
            LEFT JOIN INVENTARIO_CLASE C ON A.ID_CLASE = C.ID_CLASE
            LEFT JOIN INVENTARIO_MARCA D ON A.ID_MARCA = D.ID_MARCA
   ORDER BY A.ID_ARTICULO;

