CREATE OR REPLACE VIEW vw_persona_natural (id_persona, paterno, materno, nombre, sexo, fec_nacimiento, nom_persona, id_tipodocumento, num_documento) AS SELECT
  Persona.ID_PERSONA,
  Persona.PATERNO,
  Persona.MATERNO,
  Persona.NOMBRE,
  Persona_Natural.SEXO,
  Persona_Natural.FEC_NACIMIENTO,
  UPPER(NVL(Persona.PATERNO || ' ' , '') || NVL(Persona.MATERNO || ', ', '')) || /*dbo.fn_NomPropio(*/
Persona.NOMBRE/*)*/
 AS NOM_PERSONA,
  Persona_Documento.ID_TIPODOCUMENTO,
  Persona_Documento.NUM_DOCUMENTO
FROM Persona
INNER JOIN Persona_Natural ON
  Persona.ID_PERSONA = Persona_Natural.ID_PERSONA
INNER JOIN Persona_Documento ON
  Persona.ID_PERSONA = Persona_Documento.ID_PERSONA 
