CREATE OR REPLACE VIEW vw_rep_cuentas (id_cuenta, id_parent, id_entidad, id_anho, id_tiporeporte, nombre, orden, id_cuentaaasi) AS SELECT A.ID_CUENTA,
          A.ID_PARENT,
          A.ID_ENTIDAD,
          B.ID_ANHO,
          A.ID_TIPOREPORTE,
          A.NOMBRE,
          A.ORDEN,
          B.ID_CUENTAAASI
     FROM CONTA_CUENTA A, CONTA_CUENTA_DETALLE B
    WHERE     A.ID_CUENTA = B.ID_CUENTA
          AND B.ID_CDETALLE NOT IN (SELECT ID_CDETALLE
                                      FROM CONTA_CUENTA_CTE);

