CREATE OR REPLACE VIEW vw_sales_mov_aces (id_entidad, id_depto, id_anho, id_mes, id_venta, id_cliente, id_sucursal, id_comprobante, id_moneda, serie, numero, fecha, glosa, total, total_me, id_tipotransaccion, id_tipoventa, tipo, mov, voucher, lote, tipo_documento, numero_legal, id_tipoorigen, id_origen, tipocambio) AS SELECT 	  A.ID_ENTIDAD,
