     FROM CAJA_DEPOSITO A, CAJA_DEPOSITO_DETALLE B, VENTA C, CONTA_VOUCHER D
    WHERE A.ID_DEPOSITO = B.ID_DEPOSITO AND B.ID_VENTA = C.ID_VENTA AND A.ID_CLIENTE = C.ID_CLIENTE AND A.ID_VOUCHER = D.ID_VOUCHER AND B.IMPORTE <> 0   
