     FROM VENTA_TRANSFERENCIA A, VENTA_TRANSFERENCIA_DETALLE B, VENTA C
    WHERE A.ID_TRANSFERENCIA = B.ID_TRANSFERENCIA AND B.ID_VENTA_DESTINO = C.ID_VENTA 
