          A.SERIE||'-'||A.NUMERO AS NUMERO_LEGAL,
          A.ID_TIPOORIGEN,
          A.ID_VENTA AS ID_ORIGEN,
          A.TIPOCAMBIO AS TIPOCAMBIO
     FROM VENTA A LEFT JOIN CONTA_VOUCHER B
     ON A.ID_VOUCHER = B.ID_VOUCHER
    WHERE     A.ID_COMPROBANTE IN ('07', '08')
      AND A.ID_PARENT IS NULL
      AND A.ID_SALDO IS NULL
      AND A.ID_TRANSFERENCIA IS NULL
      AND A.ESTADO = 1
      AND A.TOTAL <> 0
      UNION ALL
   SELECT A.ID_ENTIDAD,
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          C.ID_VENTA,
          A.ID_PROVEEDOR,
          C.ID_SUCURSAL,
          C.ID_COMPROBANTE,
          A.ID_MONEDA,
          C.SERIE,
          C.NUMERO,
          C.FECHA,
          SUBSTR (B.DETALLE, 0, 50) AS GLOSA,
          B.IMPORTE * -1 AS TOTAL,
          B.IMPORTE_ME * -1 AS TOTAL_ME,
          C.ID_TIPOTRANSACCION,
          C.ID_TIPOVENTA,
          'D' AS TIPO,
          'DET.-' || A.NRO_OPERACION AS MOV,
          TO_CHAR (D.NUMERO) AS VOUCHER,
          D.LOTE,
          '99' AS TIPO_DOCUMENTO,
          A.NUMERO || '-' || A.NRO_OPERACION AS NUMERO_LEGAL,
          A.ID_TIPOORIGEN,
          A.ID_DETRACCION AS ID_ORIGEN,
          NULL AS TIPOCAMBIO
     FROM CAJA_DETRACCION A
          JOIN CAJA_DETRACCION_VENTA B ON A.ID_DETRACCION = B.ID_DETRACCION
          JOIN VENTA C ON B.ID_VENTA = C.ID_VENTA AND A.ID_ANHO = C.ID_ANHO
          LEFT JOIN CONTA_VOUCHER D ON A.ID_VOUCHER = D.ID_VOUCHER
    WHERE A.ESTADO = '1'
    UNION ALL
	SELECT B.ID_ENTIDAD,
	  B.ID_DEPTO,
	  B.ID_ANHO,
	  B.ID_MES,
	  C.ID_VENTA,
	  B.ID_CLIENTE,
	  C.ID_SUCURSAL,
	  C.ID_COMPROBANTE,
	  A.ID_MONEDA,
	  C.SERIE,
	  C.NUMERO,
	  C.FECHA,
	  SUBSTR ('AJUSTE DEPOSITO', 0, 50) AS GLOSA,
	  A.IMPORTE * DECODE(A.DC,'C',-1,1) AS IMPORTE,
	  A.IMPORTE_ME * DECODE(A.DC,'C',-1,1) AS IMPORTE_ME,
	  B.ID_TIPOTRANSACCION,
	  C.ID_TIPOVENTA,
	  'V' AS TIPO,
      'AJUST: '||B.SERIE||'-'||B.NUMERO AS MOV,
      TO_CHAR(D.NUMERO) AS VOUCHER,
      D.LOTE,
      '00' AS TIPO_DOCUMENTO,
      B.SERIE||'-'||B.NUMERO AS NUMERO_LEGAL,
      A.ID_TIPOORIGEN,
      A.ID_DAJUSTE AS ID_ORIGEN,
      B.TIPOCAMBIO AS TIPOCAMBIO
    FROM CAJA_DEPOSITO_AJUSTE A, CAJA_DEPOSITO B,VENTA C, CONTA_VOUCHER D 
    WHERE A.ID_DEPOSITO = B.ID_DEPOSITO AND A.ID_VENTA = C.ID_VENTA AND A.ID_VOUCHER = D.ID_VOUCHER
    AND A.ESTADO = 1;

