CREATE OR REPLACE FUNCTION enoc.fc_est_aprob_req_t (P_ID_SOLIC_REQUE bigint, P_ID_ESTADO_REQ text) RETURNS varchar AS $body$
DECLARE

l_dato varchar(5):='N';
l_contar integer:=0;
l_id_estado_act varchar(5);
l_id_estado varchar(5);
l_id_tipo_paso bigint;
l_orden bigint;
l_id_entidad bigint;

BEGIN

     SELECT ID_ESTADO_REQ, ID_PASO_REQUE, ID_ENTIDAD
     INTO STRICT l_id_estado, l_id_tipo_paso,l_id_entidad
     FROM enoc.PLLA_SOLIC_REQUE
     WHERE ID_SOLIC_REQUE=P_ID_SOLIC_REQUE;

 
     SELECT COUNT(*) INTO STRICT l_contar 
     FROM enoc.PLLA_PASO_REQUE_EST 
     WHERE ID_PASO_REQUE=l_id_tipo_paso
     AND ID_ESTADO_REQ=l_id_estado
     AND ID_ENTIDAD =l_id_entidad;

     IF l_contar>0 THEN
        
       SELECT COUNT(*) INTO STRICT l_contar 
       FROM enoc.PLLA_PASO_REQUE_EST 
       WHERE ID_PASO_REQUE=l_id_tipo_paso
       AND ID_ESTADO_REQ=P_ID_ESTADO_REQ
       AND ID_ENTIDAD =l_id_entidad;


       IF l_contar>0 THEN
       
         select orden into STRICT l_orden from ENOC.PLLA_ESTADO_REQ
         WHERE ID_ESTADO_REQ=P_ID_ESTADO_REQ;

         SELECT COUNT(*) INTO STRICT l_contar  
         FROM enoc.VW_PASO_REQUE_EST
         WHERE ID_PASO_REQUE=l_id_tipo_paso
         AND ID_ENTIDAD= l_id_entidad
         AND orden<l_orden
         ORDER BY orden DESC;

         IF l_contar>0 THEN
           SELECT max(ID_ESTADO_REQ) INTO STRICT l_id_estado_act 
           FROM enoc.VW_PASO_REQUE_EST
           WHERE ID_PASO_REQUE=l_id_tipo_paso
           AND ID_ENTIDAD= l_id_entidad
           AND orden<l_orden
          -- AND  ROWNUM = 1
           ORDER BY orden DESC;

           if l_id_estado_act = l_id_estado then
              l_dato:='S';
           end if;
         END IF;
        END IF;
     END IF;

	RETURN(l_dato);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
