CREATE OR REPLACE FUNCTION enoc.fc_obtener_idpersona (P_NUM_DOCUMENTO text) RETURNS bigint AS $body$
DECLARE

l_dato bigint:=0;
l_contar integer:=0;


BEGIN

   select COUNT(*) INTO STRICT l_contar from MOISES.PERSONA_NATURAL WHERE NUM_DOCUMENTO=P_NUM_DOCUMENTO;

   IF l_contar>0 THEN
     IF l_contar=1 THEN
      select ID_PERSONA INTO STRICT l_dato  from MOISES.PERSONA_NATURAL WHERE NUM_DOCUMENTO=P_NUM_DOCUMENTO;
     ELSE
      select max(ID_PERSONA) into STRICT l_dato from MOISES.PERSONA_NATURAL WHERE  NUM_DOCUMENTO=P_NUM_DOCUMENTO;
     END IF;
   END IF;

	RETURN(l_dato);
END;
$body$
LANGUAGE PLPGSQL
