CREATE OR REPLACE FUNCTION enoc.fc_persona_reporte (P_ID_PERFIL_PUESTO bigint) RETURNS bigint AS $body$
DECLARE

   l_cantidad bigint;


BEGIN 
      l_cantidad := 0;WITH RECURSIVE cte AS (


     select 
      coalesce(sum((
        select count(*) from moises.trabajador x, ELISEO.ORG_SEDE_AREA y
        where y.id_sedearea = x.id_sedearea 
        and y.id_area=a.id_area 
        and y.id_entidad=a.id_entidad
        and substr(y.id_depto,1,1)=a.id_depto
        and x.id_puesto=a.id_puesto 
        )),0)
      from plla_perfil_puesto a WHERE a.id_perfil_puesto_jefe=P_ID_PERFIL_PUESTO 
  UNION ALL

            
     select 
      coalesce(sum((
        select count(*) from moises.trabajador x, ELISEO.ORG_SEDE_AREA y
        where y.id_sedearea = x.id_sedearea 
        and y.id_area=a.id_area 
        and y.id_entidad=a.id_entidad
        and substr(y.id_depto,1,1)=a.id_depto
        and x.id_puesto=a.id_puesto 
        )),0)
      from plla_perfil_puesto a JOIN cte c ON (c.prior id_perfil_puesto = a.a.id_perfil_puesto_jefe)

) SELECT * INTO STRICT  l_cantidad FROM cte;
        

      RETURN(l_cantidad);
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
