CREATE OR REPLACE FUNCTION enoc.fc_validar_aprob_req_grupo (P_ID_SOLIC_REQUE bigint,P_ID_ESTADO_REQ_CAND text, P_ID_ESTADO_REQ_CAND_APROB text) RETURNS varchar AS $body$
DECLARE

l_contar bigint:=0;
l_ret varchar(600):='N';
l_contar_apro bigint;
l_id_modalidad_req bigint;
l_esdocente bigint;
l_id_entidad bigint;
l_codigo_mod varchar(20);
l_id_situacion_trabajador varchar(10):='0';
l_cerrado_presel bigint;


BEGIN

    SELECT coalesce(cerrado_presel, 0) INTO STRICT l_cerrado_presel from enoc.plla_solic_reque  where ID_SOLIC_REQUE=P_ID_SOLIC_REQUE;

    if l_cerrado_presel=0 then
--       GOTO SALIDA;
    end if;
    select count(1) into STRICT l_contar
    from enoc.PLLA_SOLIC_REQ_CANDIDATO
    where ID_SOLIC_REQUE=P_ID_SOLIC_REQUE
    and ID_ESTADO_REQ_CAND=P_ID_ESTADO_REQ_CAND;

    if l_contar=0 then
--       GOTO SALIDA;
    end if;

    select count(1) into STRICT l_contar_apro
    from enoc.PLLA_SOLIC_REQ_CANDIDATO 
    where ID_SOLIC_REQUE=P_ID_SOLIC_REQUE
    and ID_ESTADO_REQ_CAND=P_ID_ESTADO_REQ_CAND
    and enoc.fc_est_aprob_req_cand(id_solic_req_candidato,P_ID_ESTADO_REQ_CAND_APROB)='S';

    if l_contar=l_contar_apro then
      l_ret:='S';
    end if;

--   <<SALIDA>>
	RETURN(l_ret);
END;
$body$
