CREATE OR REPLACE PROCEDURE enoc.pkg_desempeno_sp_fb_generar_evaluador (P_ID_DESEMPENO bigint,P_ID_TRABAJADOR bigint,P_ID_USER_REG bigint,P_ERROR OUT bigint, P_MSGERROR out text) AS $body$
DECLARE

        l_contar bigint;
        l_error bigint:=0;
        l_msgerror varchar(200):='';
        l_id_evaluador bigint;

      
BEGIN
        

        select coalesce(max(ID_EVALUADOR),0)  into STRICT l_id_evaluador from enoc.DES_FB_EVALUADOR;

        insert into ENOC.DES_FB_EVALUADOR(
          ID_EVALUADOR,
          ID_DESEMPENO,
          ID_TRABAJADOR,
          ID_SEDEAREA,
          ID_PERFIL_PUESTO,
          ID_CONDICION_LABORAL,
          ID_TIPO_TIEMPO_TRABAJO,
          ID_USER_REG,
          FECHA_REG
        )
        SELECT (row_number() OVER ( ORDER BY x.ID_TRABAJADOR ASC )) + l_id_evaluador as ID_PARTICIPANTE,
        P_ID_DESEMPENO as ID_DESEMPENO,
        x.ID_TRABAJADOR,
        x.ID_SEDEAREA,
        x.ID_PERFIL_PUESTO,
        x.ID_CONDICION_LABORAL,
        x.ID_TIPO_TIEMPO_TRABAJO,
        P_ID_USER_REG,
        clock_timestamp()
        from (
          
          SELECT 
          ID_TRABAJADOR,
          ID_SEDEAREA,
          ID_PERFIL_PUESTO,
          ID_CONDICION_LABORAL,
          ID_TIPO_TIEMPO_TRABAJO
          from moises.trabajador 
          where ID_TRABAJADOR= P_ID_TRABAJADOR 
          and ID_TRABAJADOR not in (
            select ID_TRABAJADOR from enoc.DES_FB_EVALUADOR
            where ID_DESEMPENO=P_ID_DESEMPENO
          ) 
          
        )x;

--         <<salida>>
        P_ERROR:=l_error;
        P_MSGERROR:=l_msgerror;

      END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
