SET client_encoding TO 'UTF8';
SET search_path = enoc,pkg_selection,public;




CREATE OR REPLACE PROCEDURE enoc.pkg_selection_sp_gen_plantilla_aprob (P_ID_SOLIC_REQ_CANDIDATO_ES bigint,P_ERROR OUT bigint,P_MSGERROR out text) AS $body$
DECLARE

        l_contar bigint;
        l_error bigint:=0;
        l_msgerror varchar(200):='';
        l_plantilla text:='';
        l_mensajeo text:='';
        l_parametro  text:='';
        l_longitud bigint:=0;
        l_id_req_cand_plantilla bigint;
        l_id_estado_req_cand bigint;
        l_id_solic_req_candidato bigint;
        curpara CURSOR FOR
        SELECT 
          ID_REQ_CAND_PARAMETRO,
          TIPO,
          PARAMETRO,
          SIGNIFICADO,
          COMENTARIO,
          CAMPO,
          PROCESO,
          VIGENCIA
        from PLLA_REQ_CAND_PARAMETRO
        WHERE TIPO_MEN IN ('AM','AP')
        AND VIGENCIA=1 
        ORDER BY ID_REQ_CAND_PARAMETRO;

      
BEGIN
      
        select id_req_cand_plantilla, id_solic_req_candidato 
        INTO STRICT l_id_req_cand_plantilla,l_id_solic_req_candidato 
        from enoc.plla_solic_req_candidato_est 
        WHERE ID_SOLIC_REQ_CANDIDATO_ES=P_ID_SOLIC_REQ_CANDIDATO_ES;

        --l_id_plantilla_contrato:=5;
        
        SELECT COUNT(*) INTO STRICT l_contar  from PLLA_REQ_CAND_PLANTILLA where id_req_cand_plantilla=l_id_req_cand_plantilla;

        IF l_contar=0 THEN
            l_error:=1;
            l_msgerror:='No esta asignado en la configurado de plantilla';
--             goto salida_proc;
        END IF;

        select APROBACION,coalesce(length(APROBACION),0) INTO STRICT l_plantilla,l_longitud from PLLA_REQ_CAND_PLANTILLA where id_req_cand_plantilla=l_id_req_cand_plantilla;

        
        IF coalesce(l_plantilla::text, '') = '' THEN
            l_error:=1;
            l_msgerror:='No esta configurado plantilla';
--             goto salida_proc;
        END IF;

        FOR cur in curpara LOOP
        BEGIN
          if cur.tipo='F' then
             EXECUTE 'SELECT '||cur.campo||'  from ENOC.VW_SOLIC_REQ_CANDIDATO a where a.id_solic_req_candidato=:P_ID' into STRICT l_parametro USING l_id_solic_req_candidato;

             
          else
          
            EXECUTE 'BEGIN PKG_SELECTION.'||cur.proceso||'(:P_ID,:P_PARAMETRO,:l_parametro,:l_error,:l_msgerror); end;'
            USING l_id_solic_req_candidato,cur.parametro,IN OUT l_parametro,IN OUT l_error,IN OUT l_msgerror;

              
          end if;
          --dbms_output.put_line(l_plantilla||' '||cur.parametro||'='||l_parametro||'-'||l_error);
          /*if l_parametro is  null then 
            l_parametro:='{'||cur.parametro||'}';
          end if;*/
          l_plantilla:=replace(l_plantilla,'['||cur.parametro||']',l_parametro);
        END;
        END LOOP;

        IF l_error=0 THEN
          UPDATE enoc.plla_solic_req_candidato_est SET
          MENSAJE = l_plantilla
          WHERE ID_SOLIC_REQ_CANDIDATO_ES=P_ID_SOLIC_REQ_CANDIDATO_ES;
        END IF;

--         <<salida_proc>>
        P_ERROR:=l_error;
        P_MSGERROR:=l_msgerror;
  END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE enoc.pkg_selection_sp_gen_plantilla_aprob (P_ID_SOLIC_REQ_CANDIDATO_ES bigint,P_ERROR OUT bigint,P_MSGERROR out text) FROM PUBLIC;
