SET client_encoding TO 'UTF8';

SET search_path = enoc,public;
\set ON_ERROR_STOP ON

function                                     FC_EST_APROB_REQ(P_ID_SOLIC_REQUE NUMBER, P_ID_ESTADO_REQ VARCHAR2) RETURN VARCHAR2
IS
l_dato VARCHAR2(5):='N';
l_contar int:=0;
l_id_estado_act VARCHAR2(5);
l_id_estado VARCHAR2(5);
l_id_tipo_paso number;
l_orden number;
l_id_entidad number;
BEGIN

     SELECT ID_ESTADO_REQ
     INTO l_id_estado
     FROM enoc.PLLA_SOLIC_REQUE 
     WHERE ID_SOLIC_REQUE=P_ID_SOLIC_REQUE;
     
     select orden into l_orden from ENOC.PLLA_ESTADO_REQ
     WHERE ID_ESTADO_REQ=P_ID_ESTADO_REQ
     and vigencia=1;
     
     SELECT COUNT(*) INTO l_contar  
     FROM enoc.PLLA_ESTADO_REQ
     WHERE orden<l_orden
     and vigencia=1;
     
     IF l_contar>0 THEN
        SELECT max(ID_ESTADO_REQ) INTO l_id_estado_act 
           FROM enoc.PLLA_ESTADO_REQ
           WHERE orden<l_orden
           and vigencia=1;
           if l_id_estado_act = l_id_estado then
              l_dato:='S';
           end if;
      end if;

	RETURN(l_dato);
END;

