CREATE OR REPLACE FUNCTION enoc.fc_est_aprob_adicional (P_ID_ADICIONAL bigint, P_ID_ESTADO_ADICIONAL text) RETURNS varchar AS $body$
DECLARE

l_dato varchar(5):='N';
l_contar integer:=0;
l_id_estado_act varchar(5);
l_id_estado varchar(5);
l_id_tipo_concep_adic bigint;
l_orden bigint;

BEGIN

     SELECT ID_ESTADO_ADICIONAL, ID_TIPO_CONCEP_ADIC
     INTO STRICT l_id_estado, l_id_tipo_concep_adic
     FROM enoc.VW_ADICIONAL
     WHERE ID_ADICIONAL=P_ID_ADICIONAL;

 
     SELECT COUNT(*) INTO STRICT l_contar 
     FROM enoc.PLLA_TIPO_CONCEP_ADIC_EST 
     WHERE ID_TIPO_CONCEP_ADIC=l_id_tipo_concep_adic
     AND ID_ESTADO_ADICIONAL=l_id_estado;

     IF l_contar>0 THEN
        
       SELECT COUNT(*) INTO STRICT l_contar 
       FROM enoc.PLLA_TIPO_CONCEP_ADIC_EST 
       WHERE ID_TIPO_CONCEP_ADIC=l_id_tipo_concep_adic
       AND ID_ESTADO_ADICIONAL=P_ID_ESTADO_ADICIONAL;


       IF l_contar>0 THEN
       
         select orden into STRICT l_orden from ENOC.PLLA_ESTADO_ADICIONAL
         WHERE ID_ESTADO_ADICIONAL=P_ID_ESTADO_ADICIONAL;

         SELECT coalesce(MAX(A.ID_ESTADO_ADICIONAL),'-') INTO STRICT l_id_estado_act 
         FROM enoc.PLLA_TIPO_CONCEP_ADIC_EST A, ENOC.PLLA_ESTADO_ADICIONAL B
         WHERE A.ID_ESTADO_ADICIONAL=B.ID_ESTADO_ADICIONAL
         AND A.ID_TIPO_CONCEP_ADIC=l_id_tipo_concep_adic
         AND B.orden<l_orden;

         if l_id_estado_act = l_id_estado then
            l_dato:='S';
         end if;
        END IF;
     END IF;

	RETURN(l_dato);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
