CREATE OR REPLACE FUNCTION enoc.fc_split (p_list text, p_del text DEFAULT ',') RETURNS SETOF TABLASTRING AS $body$
DECLARE

   l_idx integer;
   l_list text:= p_list;

BEGIN
   LOOP
      l_idx := position(p_del in l_list);

      IF l_idx > 0 THEN
         RETURN NEXT SUBSTR(l_list, 1, l_idx - 1);
         l_list := SUBSTR(l_list, l_idx + LENGTH(p_del));
      ELSE
         RETURN NEXT l_list;
         EXIT;
      END IF;

   END LOOP;

   RETURN;
END;
$body$
