SET client_encoding TO 'UTF8';
SET search_path = enoc,pkg_contract,public;




CREATE OR REPLACE PROCEDURE enoc.pkg_contract_sp_copiar_contrato_plantilla (P_ID_CONTRATO_PLANTILLA bigint,P_ID_ENTIDAD_TO bigint,P_ID_DEPTO_TO text, P_ID_USER bigint,P_ERROR OUT bigint,P_MSGERROR OUT text) AS $body$
DECLARE

		L_ERROR bigint:=0;
		L_MSGERROR varchar(5000):='';
		L_CONTAR bigint:=0;
		L_ID_CONTRATO_PLANTILLA bigint:=0;
		L_NOMBRE varchar(200):='';
		L_ID_ENTIDAD bigint:=0;
		L_ID_DEPTO varchar(10):='';
		L_CONTAR_REG bigint:=0;
	
  ITEM RECORD;

BEGIN
		-- ha tener en cuenta que sólo se copiará a los deptos padres que estan dentro de la tabla ELISEO.ORG_SEDE_AREA
		 FOR ITEM IN (
		   		SELECT DISTINCT A.ID_ENTIDAD,A.ID_DEPTO FROM ELISEO.CONTA_ENTIDAD_DEPTO A
				INNER JOIN ELISEO.VW_SEDE_AREA B ON A.ID_ENTIDAD = B.ID_ENTIDAD AND A.ID_DEPTO = B.ID_DEPTO_PADRE
				WHERE A.ID_ENTIDAD = P_ID_ENTIDAD_TO AND A.ES_GRUPO = '1' AND LENGTH(A.ID_DEPTO) = 1
				AND 1 = (CASE WHEN P_ID_DEPTO_TO = '0' THEN 1 ELSE CASE WHEN A.ID_DEPTO = P_ID_DEPTO_TO THEN 1 ELSE 0 END END)
				AND A.ID_DEPTO <> '0' )
		 LOOP
			 L_CONTAR_REG := L_CONTAR_REG + 1;
			 SELECT NOMBRE,ID_ENTIDAD,ID_DEPTO INTO STRICT L_NOMBRE,L_ID_ENTIDAD,L_ID_DEPTO FROM ENOC.PLLA_CONTRATO_PLANTILLA WHERE ID_CONTRATO_PLANTILLA = P_ID_CONTRATO_PLANTILLA  LIMIT 1;
					
			 IF (ITEM.ID_ENTIDAD = L_ID_ENTIDAD AND ITEM.ID_DEPTO <> L_ID_DEPTO ) OR ITEM.ID_ENTIDAD <> L_ID_ENTIDAD THEN
				
				 SELECT COUNT(*) INTO STRICT L_CONTAR FROM ENOC.PLLA_CONTRATO_PLANTILLA 
				 WHERE UPPER(trim(both NOMBRE)) = UPPER(trim(both L_NOMBRE)) AND ID_ENTIDAD = ITEM.ID_ENTIDAD AND ID_DEPTO = ITEM.ID_DEPTO;
				 
				IF L_CONTAR > 0 THEN 
					L_ERROR := 1;
					L_MSGERROR := 'Ya existe registrado la plantilla "'||L_NOMBRE||'" con el mismo nombre en la entidad  "'||ITEM.ID_ENTIDAD||'".';
-- 					GOTO salida_val;
				END IF;
				
				select coalesce(max(ID_CONTRATO_PLANTILLA),0)+1 into STRICT L_ID_CONTRATO_PLANTILLA from enoc.PLLA_CONTRATO_PLANTILLA;
				
				 INSERT INTO ENOC.PLLA_CONTRATO_PLANTILLA(ID_CONTRATO_PLANTILLA,ID_ENTIDAD,ID_DEPTO,ID_TIPO_CONTRATO,NOMBRE,DESCRIPCION,PLANTILLA,ID_USER_REG,FECHA_REG,VIGENCIA)
				 SELECT L_ID_CONTRATO_PLANTILLA AS ID_CONTRATO_PLANTILLA,ITEM.ID_ENTIDAD AS ID_ENTIDAD,ITEM.ID_DEPTO AS ID_DEPTO,ID_TIPO_CONTRATO,NOMBRE,DESCRIPCION,PLANTILLA,
				P_ID_USER AS ID_USER_REG,CURRENT_DATE AS FECHA_REG, VIGENCIA
				FROM ENOC.PLLA_CONTRATO_PLANTILLA WHERE ID_CONTRATO_PLANTILLA = P_ID_CONTRATO_PLANTILLA;
			END IF;
		 END LOOP;
		
		IF L_CONTAR_REG = 0 THEN
			L_ERROR := 2;
			L_MSGERROR := 'No se ha copiado. Sólo se permite copiar a los deptos padres, de los centros de costo registrados.';
		END IF;
		
-- 		<<salida_val>> 
	    P_ERROR:= L_ERROR;
	    P_MSGERROR:= L_MSGERROR;
	END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE enoc.pkg_contract_sp_copiar_contrato_plantilla (P_ID_CONTRATO_PLANTILLA bigint,P_ID_ENTIDAD_TO bigint,P_ID_DEPTO_TO text, P_ID_USER bigint,P_ERROR OUT bigint,P_MSGERROR OUT text) FROM PUBLIC;
