SET client_encoding TO 'UTF8';
SET search_path = enoc,pkg_planilla_proc,public;




CREATE OR REPLACE PROCEDURE enoc.pkg_planilla_proc_sp_a_essalud (P_ID_PLANILLA_TRAB text,P_ID_CONFIG_PLANILLA_CONCEPTO bigint, P_IMPORTE OUT bigint,P_ERROR OUT bigint,P_MSGERROR out text) AS $body$
DECLARE

  l_contar bigint;
  l_error bigint:=0;
  l_msgerror varchar(200):='';
  l_importe decimal(10,2):=0;
  l_id_trabajador bigint;
  l_id_anho bigint;
  l_id_mes bigint;
  l_num_dias bigint;

  l_id_concepto_planilla bigint;
  l_id_persona bigint;
  l_id_entidad bigint;
  l_porc decimal(10,3):=0;
  l_importe_cal decimal(10,2):=0;


BEGIN
    select 
    id_trabajador,  id_anho,  id_mes,  coalesce(num_dias,0),id_entidad
    INTO STRICT
    l_id_trabajador,  l_id_anho,  l_id_mes,   l_num_dias,l_id_entidad
    FROM ENOC.VW_PLANILLA_TRAB
    WHERE ID_PLANILLA_TRAB=P_ID_PLANILLA_TRAB;

    select 
    a.id_persona
    into STRICT 
    l_id_persona
    from moises.PERSONA_NATURAL_TRABAJADOR a,
    moises.trabajador b 
    where a.id_persona=b.id_persona
    and b.id_trabajador=l_id_trabajador;

    select id_concepto_planilla into STRICT l_id_concepto_planilla 
    from ENOC.PLLA_CONFIG_PLANILLA_CONCEPTO 
    where ID_CONFIG_PLANILLA_CONCEPTO=P_ID_CONFIG_PLANILLA_CONCEPTO;

    select count(*) into STRICT  l_contar from ENOC.VW_PLLA_PARAMETROS_VALOR where id_entidad= l_id_entidad and codigo='PARAM_ESSALUD' and id_anho=l_id_anho;
    if l_contar=0 then
      l_error:=1;
      l_msgerror:='Falta configurar parametro ESSALUD';
--       goto salida;
    end if;

    if l_contar>0 then
      select importe into STRICT l_porc from ENOC.VW_PLLA_PARAMETROS_VALOR where id_entidad= l_id_entidad and codigo='PARAM_ESSALUD' and id_anho=l_id_anho;
    end if;

    if l_porc<=0 then
      l_error:=1;
      l_msgerror:='Parametro ESSALUD es cero';
--       goto salida;
    end if;

    select coalesce(sum(importe),0) into STRICT l_importe_cal from enoc.VW_PLLA_PLANILLA_TRAB_DET 
    where ID_PLANILLA_TRAB=P_ID_PLANILLA_TRAB
    and id_tipo_concepto='I'
    and coalesce(EMP_ESSALUD,'0')='1';

    l_importe:=round((l_importe_cal*l_porc)::numeric,2);

      
    CALL pkg_planilla_sp_insmod_planilla_trab_det(
          P_ID_PLANILLA_TRAB,
          l_id_concepto_planilla,
          l_importe,
          l_num_dias,
          0,
          0,
          0,
          l_importe_cal,
          l_porc,
          0
      );

--     <<salida>>
    P_IMPORTE:=l_importe;
    P_ERROR:=l_error;
    P_MSGERROR:=l_msgerror;
  END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE enoc.pkg_planilla_proc_sp_a_essalud (P_ID_PLANILLA_TRAB text,P_ID_CONFIG_PLANILLA_CONCEPTO bigint, P_IMPORTE OUT bigint,P_ERROR OUT bigint,P_MSGERROR out text) FROM PUBLIC;
