SET client_encoding TO 'UTF8';

SET search_path = enoc,public;
\set ON_ERROR_STOP ON

function           FC_EST_APROB_CAMBIO(P_ID_CAMBIOS NUMBER, P_ID_ESTADO_APRO VARCHAR2) RETURN VARCHAR2
IS
l_dato VARCHAR2(5):='N';
l_contar int:=0;
l_id_estado_act VARCHAR2(5);
l_id_estado VARCHAR2(5);
l_proceso_fin  VARCHAR2(5):='N';
l_id_concepto_cambio number;
l_orden number;
BEGIN

     SELECT ID_ESTADO_CAMBIO, coalesce(PROCESO_FIN,'N') ,ID_CONCEPTO_CAMBIO
     INTO l_id_estado, l_proceso_fin ,l_id_concepto_cambio
     FROM enoc.PLLA_CAMBIOS 
     WHERE ID_CAMBIOS=P_ID_CAMBIOS;
     
     if l_proceso_fin='N' then

       SELECT COUNT(*) INTO l_contar 
       FROM enoc.VW_CONCEPTO_ESTADO_CAMBIO 
       WHERE ID_CONCEPTO_CAMBIO=l_id_concepto_cambio
       AND ID_ESTADO_CAMBIO=l_id_estado;
       
       IF l_contar>0 THEN
          
         SELECT COUNT(*) INTO l_contar 
         FROM enoc.VW_CONCEPTO_ESTADO_CAMBIO 
         WHERE ID_CONCEPTO_CAMBIO=l_id_concepto_cambio
         AND ID_ESTADO_CAMBIO=P_ID_ESTADO_APRO;
       
 
         IF l_contar>0 THEN
         
           select orden into l_orden from ENOC.PLLA_CONCEPTO_ESTADO_CAMBIO
           WHERE ID_CONCEPTO_CAMBIO=l_id_concepto_cambio
           AND ID_ESTADO_CAMBIO=P_ID_ESTADO_APRO;
          
           SELECT coalesce(MAX(ID_ESTADO_CAMBIO),'-') INTO l_id_estado_act 
           FROM enoc.VW_CONCEPTO_ESTADO_CAMBIO
           WHERE ID_CONCEPTO_CAMBIO=l_id_concepto_cambio
           AND orden_cambio<l_orden;
           
           if l_id_estado_act = l_id_estado then
              l_dato:='S';
           end if;
          END IF;
       END IF;
    end if;
	RETURN(l_dato);
END;

