SET client_encoding TO 'UTF8';

SET search_path = enoc,public;
\set ON_ERROR_STOP ON

function FC_EST_APROB_CONTRATO(P_ID_ENTIDAD NUMBER, P_ID_DEPTO VARCHAR2, P_TIPO VARCHAR2, P_ID_ESTADO_CONT VARCHAR2, P_ID_ESTADO_APRO VARCHAR2) RETURN VARCHAR2
IS
l_dato VARCHAR2(5):='N';
l_contar int:=0;
l_id_estado_cont VARCHAR2(5);

BEGIN

     SELECT COUNT(*) INTO l_contar 
     FROM PLLA_ESTADO_CONT_DEPTO a, PLLA_ESTADO_CONT b 
     WHERE a.ID_ESTADO_CONT=b.ID_ESTADO_CONT 
     AND  a.ID_ENTIDAD=P_ID_ENTIDAD 
     AND a.ID_DEPTO=P_ID_DEPTO 
     AND a.TIPO=P_TIPO 
     AND b.ID_ESTADO_CONT=P_ID_ESTADO_CONT;
     
     IF l_contar>0 THEN
       SELECT COUNT(*) INTO l_contar 
       FROM PLLA_ESTADO_CONT_DEPTO a, PLLA_ESTADO_CONT b 
       WHERE a.ID_ESTADO_CONT=b.ID_ESTADO_CONT 
       AND  a.ID_ENTIDAD=P_ID_ENTIDAD 
       AND a.ID_DEPTO=P_ID_DEPTO 
       AND a.TIPO=P_TIPO 
       AND b.ID_ESTADO_CONT=P_ID_ESTADO_APRO;

       IF l_contar>0 THEN
        
         SELECT MAX(b.ID_ESTADO_CONT) INTO l_id_estado_cont 
         FROM PLLA_ESTADO_CONT_DEPTO a, PLLA_ESTADO_CONT b 
         WHERE a.ID_ESTADO_CONT=b.ID_ESTADO_CONT 
         AND  a.ID_ENTIDAD=P_ID_ENTIDAD 
         AND a.ID_DEPTO=P_ID_DEPTO 
         AND a.TIPO=P_TIPO 
         AND b.ID_ESTADO_CONT<P_ID_ESTADO_APRO;
         
         if l_id_estado_cont = P_ID_ESTADO_CONT then
            l_dato:='S';
         end if;
        END IF;
     END IF;
  
	RETURN(l_dato);
END;

