SET client_encoding TO 'UTF8';

SET search_path = enoc,public;
\set ON_ERROR_STOP ON

function                  FC_OBTENER_IDPERSONA(P_NUM_DOCUMENTO VARCHAR2) RETURN NUMBER
IS
l_dato NUMBER:=0;
l_contar int:=0;

BEGIN
  
   select COUNT(*) INTO l_contar from MOISES.PERSONA_NATURAL WHERE NUM_DOCUMENTO=P_NUM_DOCUMENTO ;
   
   IF l_contar>0 THEN
     IF l_contar=1 THEN
      select ID_PERSONA INTO l_dato  from MOISES.PERSONA_NATURAL WHERE NUM_DOCUMENTO=P_NUM_DOCUMENTO;
     ELSE
      select max(ID_PERSONA) into l_dato from MOISES.PERSONA_NATURAL WHERE  NUM_DOCUMENTO=P_NUM_DOCUMENTO;
     END IF;
   END IF;
  
	RETURN(l_dato);
END;

