SET client_encoding TO 'UTF8';

SET search_path = enoc,public;
\set ON_ERROR_STOP ON

FUNCTION      FC_PERSONA_REPORTE(P_ID_PERFIL_PUESTO IN NUMBER) 
   RETURN NUMBER 
   IS
   l_cantidad number;

   BEGIN 
      l_cantidad := 0;
            
     select 
      coalesce(sum((
        select count(*) from moises.trabajador x, ELISEO.ORG_SEDE_AREA y
        where y.id_sedearea = x.id_sedearea 
        and y.id_area=a.id_area 
        and y.id_entidad=a.id_entidad
        and substr(y.id_depto,1,1)=a.id_depto
        and x.id_puesto=a.id_puesto
        )),0) into  l_cantidad
      from plla_perfil_puesto a
      connect by prior a.id_perfil_puesto = a.id_perfil_puesto_jefe 
      start with  a.id_perfil_puesto_jefe=P_ID_PERFIL_PUESTO ;  
        

      RETURN(l_cantidad); 
    END;

