        and CASE WHEN P_ID_PARTICIPANTE =0 THEN 0 ELSE id_participante END = CASE WHEN P_ID_PARTICIPANTE =0 THEN 0 ELSE P_ID_PARTICIPANTE END
        order by id_participante;
        
        CURSOR curTipo IS
