          )
          and MOD ( FLOOR ( MONTHS_BETWEEN ( TRUNC( l_fecha_inicio ), TRUNC ( a.fecha_fin_efectivo ) ) ), 12 )<=1  --hasta un mes de antigüidad
          */
                   
        )x;
 
        <<salida>>
