CREATE OR REPLACE VIEW vw_adicional_det_mes (id_mes_ini, id_mes_fin, importe, id_adicional) AS select  min(x.id_mes) as id_mes_ini, max(x.id_mes) as id_mes_fin, sum(importe) as importe,x.id_adicional
                 FROM enoc.plla_adicional_det x 
                 group by  x.id_adicional;

