CREATE OR REPLACE FUNCTION genesis.ft_celular ( p_id_persona bigint ) RETURNS varchar AS $body$
DECLARE

    r_numero varchar(100);


BEGIN

	SELECT  pt.NUM_TELEFONO::text INTO STRICT  r_numero 
	FROM genesis.PERSONA_TELEFONO pt 
	WHERE pt.id_persona = p_id_persona AND  ES_ACTIVO =1  LIMIT 1;
    RETURN r_numero;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
