CREATE OR REPLACE FUNCTION genesis.ft_cita ( p_id_persona_interes bigint ) RETURNS timestamp(0) AS $body$
DECLARE

    r_fecha timestamp(0);


   

BEGIN
	SELECT t.fecha_Cita INTO STRICT  r_fecha  FROM (
	SELECT  fecha_cita , row_number() over ( order by FECHA_CITA asc ) as rnum  
	FROM genesis.PERSONA_INTERES_CITA pic 
	WHERE pic.ID_PERSONA_INTERES = p_id_persona_interes AND  EStado='0' 
	ORDER BY  fecha_Cita asc) t  WHERE t.rnum=1 
	
	
	;
    RETURN r_fecha;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
